/* html.dark is optional since dark is the default, but keep for explicitness */
html.dark {
  --background: #0a0a0a;
  --foreground: #ededed;
}

/* Make Tailwind "neutral" text classes readable in light mode by mapping them
   to the foreground variable (dark text). These use !important to override
   Tailwind's generated styles when `html.light` is present. */
html.light .text-neutral-300 { color: var(--foreground) !important; }
html.light .text-neutral-400 { color: rgba(23,23,23,0.85) !important; }
html.light .text-neutral-200 { color: rgba(23,23,23,0.95) !important; }

